package book;

public class ComparisonOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//if you have 2 number how to compare them
		
		
		int a =10, b=10;
		
		//chechking for equality --> ==
		System.out.println("is a and b equal? " + (a==b));
		
		//chechking for inequality --> !=
		System.out.println("is a and b not equal? " + (a!=b));
		
		//chechking for greater or equal --> >=
		System.out.println("is a greater or equal to b? " + (a>=b));
		
		//chechking for greater --> >
		System.out.println("is a greater than b? " + (a>b));
		
		//chechking for less or equal --> <=
		System.out.println("is a less or equal to b? " + (a<=b));
		
		//chechking for less --> <
		System.out.println("is a less than b? " + (a<b));
		
	}

}
