package book;

public class Math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=13, j=5;
	//Task7
		System.out.println("The result of multiplication is " + (i*j)
				           +", the result of division is " + (i/j) 
				           + ", and the result of modular is " + (i%j)+".");
		
		
		//double with double = double
		double x=11;
		double y=2;
		System.out.println(x/y);
		
		//int with double = the result will be double
		int aa=12;
		double bb=5.0d;
		System.out.println(aa/bb);
	}

}
