package day20;

public class Task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//DO WHILE:
		String name = "Sharif";
		int count = name.length();
		int x =0;
		do {
			System.out.print(name.charAt(x) + " ");
			x++;

		} while (x<count);
		
		System.out.println();
		System.out.println("END");
		
	}

}
